sys_hub_input_scripts.script:
{"body":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value. 
**Order number is offset by +1 in Error Handling Section.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
var requestBody1 =
 '{"filterCriteria": [{"fieldName": "masterIdentityId","operator": "=","value": "' + fd_data.action_inputs.master_user_id + '"}],"projectionList":["id"]}';
return requestBody1.toString();"}}

{"base_url":{"scriptActive":true,"script":"/*
**Access Flow/Action data using the fd_data object. Script must return a value. 
**Order number is offset by +1 in Error Handling Section.
**Available options display upon pressing "." after fd_data
**example: var shortDesc = fd_data.trigger.current.short_description;
**return shortDesc;
*/
return gs.getProperty('x_aleen_alertsnowp.alert_api_baseUrl');"}}

sys_variable_value.value:
(function execute(inputs, outputs) {
  var updateresponseBody = inputs.response_body;
  var updatehttpStatus = inputs.status_code;
  updateResponseObj = JSON.parse(updateresponseBody);
  if (updatehttpStatus == 200 && updateResponseObj.success) {
    gs.debug("Found identity Id");
    outputs.identity_id = updateResponseObj.data[0].id;
  } else {
    gs.debug("identity Id not found");
  }
})(inputs, outputs);

