sys_variable_value.value:
(function execute(inputs, outputs) {
   var Token = null;
if (inputs.status_code == 200) {
  var responseObj = JSON.parse(inputs.response_body);
  if (null != responseObj && responseObj.success && null != responseObj.data) {
    Token = responseObj.token_type + ' ' + responseObj.data.access_token;
  }
}
outputs.token = Token;
})(inputs, outputs);

