catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    g_form.hideFieldMsg('request_for');
    if (isLoading || newValue == '') {
        return;
    } else {
        if (newValue) {
            var ga = new GlideAjax('CheckUserBadgesFromUIScript');
            ga.addParam('sysparm_name', 'getAggregate');
            ga.addParam('sysparm_req_for_parameter', newValue);
            ga.getXML(mycallback);

            function mycallback(response) {
                var answerfromSI = response.responseXML.documentElement.getAttribute("answer");
                if (answerfromSI < 1) {
                    g_form.showFieldMsg("request_for", "User should have a valid badge", "error");
                } else {
                    g_form.showFieldMsg("request_for", "User has a valid badge", "info");
                }
            }
        }
    }
}
