cmn_map_page.script:
var gr = new GlideRecordSecure("x_aleen_alertsnowp_locations");
gr.addEncodedQuery("latitudeISNOTEMPTY^longitudeISNOTEMPTY");
gr.query();
while (gr.next()) {
    var lat = gr.getValue('latitude');
    var lon = gr.getValue('longitude');
    if (lat != 0.0 && lon != 0.0) {
        var item = map.addItem(gr);
        item.latitude = gr.getValue('latitude');
        item.longitude = gr.getValue('longitude');
        item.dialog_title = gr.getValue('name');
        item.icon_width = "32";
        item.icon_height = "32";
        var county = gr.getValue('country');
