sys_variable_value.value:
(function execute(inputs, outputs) {
/*gs.info("Number "+inputs.record_number+" table "+inputs.table);*/
var gr = new GlideRecordSecure(inputs.table);
gr.initialize();
gr.get("number",inputs.record_number);
gr.query();
executeAlertApi();
function sendData(msg){
   outputs.alert_request_number=msg[0].valuesMap.id;
   outputs.alert_request_status="open";
}
function executeAlertApi() {
   gs.info("Making Api Call");
   var includeScript = new AlertEnterprisePlatformAPIs();
   if(inputs.table==="x_aleen_alertsnowp_new_badge"){
      gs.info("New Badge Request");
      sendData(JSON.parse(includeScript.newBadge(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_activate_badge"){
      gs.info("Activate Badge Request");
      sendData(JSON.parse(includeScript.activateBadge(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_reset_pin"){
      gs.info("Reset Pin Request");
      sendData(JSON.parse(includeScript.resetPin(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_update_profile"){
      gs.info("Update Profile Request");
      sendData(JSON.parse(includeScript.updateProfile(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_mobile_credential"){
      gs.info("Mobile credentials request");
      sendData(JSON.parse(includeScript.mobileCredentials(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_repalce_badge"){
      gs.info("Replace Badge Request");
      sendData(JSON.parse(includeScript.replaceBadge(gr)));
   }else if(inputs.table==="x_aleen_alertsnowp_building_access_task"){
      gs.info("Building Access Task");
      sendData(JSON.parse(includeScript.buildingAccess(gr)));
   }
}
})(inputs, outputs);

