sys_variable_value.value:
(function execute(inputs, outputs) {
var buildingGr = new GlideRecordSecure(inputs.table);
buildingGr.initialize();
buildingGr.get("number",inputs.record_number);
buildingGr.query();
gs.info("Action BRT "+inputs.record_number);
gs.info("Building Access Levels in request "+buildingGr.access_levels);
var accessLevels = buildingGr.access_levels;
var accessLevelsList = accessLevels.split(',');
var singleOwnerRecords = [];
var lowAccessLevelRecords = [];
var needApproval = false;
gs.info("Action BRT length of array "+accessLevelsList.length);
for (var i = 0; i < accessLevelsList.length; i++) {
	gs.info("Action BRT list length of AL "+accessLevelsList.length);
    needApproval = false;
	gs.info("Action BRT Ith index accessLevel "+accessLevelsList[i]);
	var accessLevelLocationGr = new GlideRecordSecure('x_aleen_alertsnowp_accesslocations');
	accessLevelLocationGr.initialize();
	gs.info("Action BRT Getting Access location from access level ");
	//if (accessLevelLocationGr.get("access_level",accessLevelsList[i])) { // SNOW-81
    if (accessLevelLocationGr.get(accessLevelsList[i])) { 
	gs.info("Action BRT got accesslevel Location record  of " + accessLevelLocationGr.getDisplayValue("access_level_name") +" "+accessLevelLocationGr.getDisplayValue("location"));
    var accessLevelGr = new GlideRecordSecure('x_aleen_alertsnowp_access_levels');
	accessLevelGr.initialize();
	gs.info("Action BRT Searching for access ->"+accessLevelLocationGr.access_level);
    if (accessLevelGr.get(accessLevelLocationGr.access_level)) {
		gs.info("Action BRT:: access level from access location "+accessLevelGr.getValue("access_level_name")+" "+accessLevelGr.getDisplayValue("access_level_name"));
        var count = 0;
        var gr = new GlideAggregate('x_aleen_alertsnowp_area_owners');
        gr.addQuery('access_level_id', accessLevelGr.access_level_id);
        gr.addAggregate('COUNT');
        gr.query();
        while (gr.next()) {
            count = gr.getAggregate('COUNT');
        }
		gs.info("Action BRT Count of area owner "+count);
        if (count == 0) {
			gs.info(accessLevelGr.access_level_name +"  has no Area owners");
			gs.info("Action BRT "+accessLevelGr.getDisplayValue("access_level_name")+"has no owner");
            if (accessLevelGr.criticality == "LOW") {
				gs.info("We have a low criticality access level with no owner");
                if (lowAccessLevelRecords.length > 0) {
                    lowAccessLevelRecords[0]['access_level_id'] = lowAccessLevelRecords[0]['access_level_id'] + "," + accessLevelGr.access_level_id;
                    lowAccessLevelRecords[0]['access_level_name'] = lowAccessLevelRecords[0]['access_level_name'] + "," + accessLevelGr.access_level_name;
					lowAccessLevelRecords[0]['sourceid'] = lowAccessLevelRecords[0]['sourceid'] + "###" + accessLevelGr.access_level_id;
                } else {
                    var lmap = {};
                    lmap['access_level_id'] = accessLevelGr.access_level_id;
                    lmap['access_level_name'] = accessLevelGr.access_level_name;
					lmap['sourceid'] = accessLevelGr.access_level_id;
					gs.info("Action BRT lmap to be pushed "+JSON.stringify(lmap));
                    lowAccessLevelRecords.push(lmap);
                }
            } else {
				gs.info("Action BRT We have a High Criticality with no owner setting to default approver");
                needApproval = true;
                foundIndex = -1;
                for (var icv = 0; icv < singleOwnerRecords.length; icv++) {
                    if (singleOwnerRecords[icv]['approver'] === "DEFAULT") {
                        foundIndex = icv;
                        break;
                    }
                }
                if (foundIndex > -1) {
                    singleOwnerRecords[foundIndex]['access_level_id'] = singleOwnerRecords[foundIndex]['access_level_id'] + "," + accessLevelGr.access_level_id;
                    singleOwnerRecords[foundIndex]['access_level_name'] = singleOwnerRecords[foundIndex]['access_level_name'] + "," + accessLevelGr.access_level_name;
					singleOwnerRecords[foundIndex]['sourceid'] = singleOwnerRecords[foundIndex]['sourceid'] + "###" + accessLevelGr.access_level_id;
                } else {
                    var mapa = {};
                    mapa['approver'] = "DEFAULT";
                    mapa['access_level_id'] = accessLevelGr.access_level_id;
                    mapa['access_level_name'] = accessLevelGr.access_level_name;
					mapa['sourceid'] = accessLevelGr.access_level_id;
					gs.info("Action BRT lmap to be pushed "+JSON.stringify(mapa));
                    singleOwnerRecords.push(mapa);
                }
            }
        } else {
            needApproval = true;
        }
        if (count == 1) {
			gs.info(accessLevelGr.access_level_name +"  has one Area owner");
			gs.info("Action BRT access level"+accessLevelGr.getDisplayValue("access_level_name")+" has one area owner");
            var grAO = new GlideRecordSecure('x_aleen_alertsnowp_area_owners');
            grAO.addQuery('access_level_id', accessLevelGr.access_level_id);
            grAO.query();
            while (grAO.next()) {
                var refSysUser = grAO.owner.getRefRecord();
                var approverID = refSysUser.sys_id;
				gs.info("Action BRT Setting Approver id "+approverID);
                indexFound = -1;
                for (var icx = 0; icx < singleOwnerRecords.length; icx++) {
                    var mmpa = singleOwnerRecords[icx];
                    if (mmpa['approver'] == approverID) {
                        indexFound = icx;
                        break;
                    }
                }
                if (indexFound > -1) {
                    singleOwnerRecords[indexFound]['access_level_id'] = singleOwnerRecords[indexFound]['access_level_id'] + "," + accessLevelGr.access_level_id;
                    singleOwnerRecords[indexFound]['access_level_name'] = singleOwnerRecords[indexFound]['access_level_name'] + "," + accessLevelGr.access_level_name;
					singleOwnerRecords[indexFound]['sourceid'] = singleOwnerRecords[indexFound]['sourceid'] + "###" + accessLevelGr.access_level_id;
                } else {
                    var map = {};
                    map['approver'] = approverID;
                    map['access_level_id'] = accessLevelGr.access_level_id;
                    map['access_level_name'] = accessLevelGr.access_level_name;
					map['sourceid'] = accessLevelGr.access_level_id;
					gs.info("Action BRT map to be pushed "+JSON.stringify(map));
                    singleOwnerRecords.push(map);
                }
            }
        }
        if (count > 1) {
			gs.info(accessLevelGr.access_level_name +"  has multiple Area owners");
			gs.info("Action BRT access level"+accessLevelGr.getDisplayValue("access_level_name")+" has multiple area owner");
			gs.info("Action BRT Record inserted in Building Access Task for multiple owners");
            var buildingAccessTask = new GlideRecordSecure('x_aleen_alertsnowp_building_access_task');
            buildingAccessTask.access_level_id = accessLevelGr.access_level_id;
            buildingAccessTask.access_level_name = accessLevelGr.access_level_name;
			buildingAccessTask.sourceid = accessLevelGr.access_level_id;
            buildingAccessTask.needapproval = needApproval;
            buildingAccessTask.parent_number = buildingGr.number;
            buildingAccessTask.access_end_date = buildingGr.access_end_date;
            buildingAccessTask.access_start_date = buildingGr.access_start_date;
            buildingAccessTask.request_for = buildingGr.request_for;
            buildingAccessTask.notes = buildingGr.notes;
            buildingAccessTask.reason = buildingGr.reason;
            buildingAccessTask.sys_created_on = new GlideDateTime();
            buildingAccessTask.sys_updated_on = new GlideDateTime();
            buildingAccessTask.insert();
        }
    }
	}else{
		gs.info("Action BRT NO ACCESS FOUND IN Access LOCATION table for access sys id"+accessLevelsList[i]);
	}
}
gs.info("Action BRT singleOwnerRecords.length "+singleOwnerRecords.length);
for (var icc = 0; icc < singleOwnerRecords.length; icc++) {
	gs.info("Action BRT Inserting Single area owner access ");
    var mapva = singleOwnerRecords[icc];
    var buildingAccTask = new GlideRecordSecure('x_aleen_alertsnowp_building_access_task');
    buildingAccTask.access_level_id = mapva['access_level_id'];
	gs.info("Action BRT Single Owner access id "+mapva['access_level_id']);
    buildingAccTask.access_level_name = mapva['access_level_name'];
	gs.info("Action BRT Single area owner Record source id "+mapva['sourceid']);
	buildingAccTask.sourceid = mapva['sourceid'];
    buildingAccTask.needapproval = true;
    buildingAccTask.parent_number = buildingGr.number;
    buildingAccTask.access_end_date = buildingGr.access_end_date;
    buildingAccTask.access_start_date = buildingGr.access_start_date;
    buildingAccTask.request_for = buildingGr.request_for;
    buildingAccTask.notes = buildingGr.notes;
    buildingAccTask.reason = buildingGr.reason;
    buildingAccTask.sys_created_on = new GlideDateTime();
    buildingAccTask.sys_updated_on = new GlideDateTime();
    buildingAccTask.insert();
    singleOwnerRecords.splice(icc, 1);
    --icc;
}
gs.info("Action BRT low access level record length "+lowAccessLevelRecords.length);
for (var iccl = 0; iccl < lowAccessLevelRecords.length; iccl++) {
	gs.info("Action BRT lowAcces level Record inserting");
    var mapval = lowAccessLevelRecords[iccl];
    var lowbuildingAccTask = new GlideRecordSecure('x_aleen_alertsnowp_building_access_task');
    lowbuildingAccTask.access_level_id = mapval['access_level_id'];
	gs.info("Action BRT low access access id "+mapval['access_level_id']);
    lowbuildingAccTask.access_level_name = mapval['access_level_name'];
	lowbuildingAccTask.sourceid = mapval['sourceid'];
	gs.info("Action BRT low level source id "+mapval['sourceid']);
    lowbuildingAccTask.needapproval = false;
    lowbuildingAccTask.parent_number = buildingGr.number;
    lowbuildingAccTask.access_end_date = buildingGr.access_end_date;
    lowbuildingAccTask.access_start_date = buildingGr.access_start_date;
    lowbuildingAccTask.request_for = buildingGr.request_for;
    lowbuildingAccTask.notes = buildingGr.notes;
    lowbuildingAccTask.reason = buildingGr.reason;
    lowbuildingAccTask.sys_created_on = new GlideDateTime();
    lowbuildingAccTask.sys_updated_on = new GlideDateTime();
    lowbuildingAccTask.insert();
    lowAccessLevelRecords.splice(iccl, 1);
    --iccl;
}
})(inputs, outputs);

