sys_script_include.script:
var CheckUserBadgesFromUIScript = Class.create();
CheckUserBadgesFromUIScript.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    getAggregate: function() {
        var req_for = this.getParameter('sysparm_req_for_parameter');
        var answer = 0;
        var gr = new GlideAggregate('x_aleen_alertsnowp_new_badge');
        gr.addQuery('state', 3);
        gr.addQuery('request_for', req_for);
        gr.addAggregate('COUNT');
        gr.query();
        while (gr.next()) {
            answer = gr.getAggregate('COUNT');
        }
        return answer;
    },
	getUserDetails: function() {
        gs.addInfoMessage('script include triggered');
        var userId = this.getParameter('userId');
        obj = {};
        var grSysUser = new GlideRecordSecure('sys_user');
        if (grSysUser.get(userId)) {
            obj.mobile_phone = grSysUser.getValue('mobile_phone');
            obj.email = grSysUser.getValue('email');
        }
        gs.addInfoMessage(obj + JSON.stringify(obj));
        return JSON.stringify(obj);
    },
    type: 'CheckUserBadgesFromUIScript'
});
