sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;
    type = body.type;
    requestId = body.requestId;
    var grAuditLog = new GlideRecordSecure('x_aleen_alertsnowp_audit_log');
    grAuditLog.get('ae_request_number', requestId);
    if (type == "RequestNewBadge") {
        gs.info("Got response for new badge request");
        var grnewbadge = new GlideRecordSecure('x_aleen_alertsnowp_new_badge');
        grnewbadge.get('number', grAuditLog.request_no);
        grnewbadge.state = 3;
        grnewbadge.update();
        var includeScript = new AlertEnterprisePlatformAPIs();
        includeScript.saveAuditLog(grnewbadge.number, "System", "Request is provisioned", requestId, " SUCCESS");

    } else if (type == "RequestBuildingAccess") {
        gs.info("Got response for Building Access request");
    } else if (type == "RequestMobileCredentials") {
        gs.info("Got response for Mobile Credentials request");
    } else if (type == "RequestReplaceBadge") {
        gs.info("Got response forReplace Badge request");
    }
})(request, response);
