function isBOInServiceNow() {
    var boApproverPropValue = gs.getProperty("x_aleen_alertsnowp.u_badge_office_in_snow");
    if (boApproverPropValue == "true" || boApproverPropValue == true) {
        gs.info('test@@ - ' + 'SNOW BO');
        return 'yes';
    } else {
        gs.info('test@@ - ' + 'HSC BO');
        return 'no';
    }
}

