answer = [];
var aclist = current.access_level_id.split(",");
var grAO = new GlideRecordSecure('x_aleen_alertsnowp_area_owners');
grAO.addQuery('access_level_id', aclist[0]);
grAO.query();
while (grAO.next()) {
	var refSysUser = grAO.owner.getRefRecord();
	answer.push(refSysUser.sys_id);
}
if(answer.length === 0){
	var defApproverSysId = gs.getProperty('x_aleen_alertsnowp.alert_default _approver_for_critical_access');
	if(defApproverSysId){
		answer.push(defApproverSysId);
	}
}

