//gs.info("::::::::::::::::"+ current.number + " ==> " + current.parent_number +" == > "+ current.state + ":::::::::::");
var grAO = new GlideRecordSecure('x_aleen_alertsnowp_building_access_task');
grAO.addQuery('parent_number', current.parent_number);
grAO.query();
var allTaskCompleted = true;
while (grAO.next()) {
    if (!(grAO.state == 3 || grAO.state == 4)) {
        allTaskCompleted = false;
    }
}
//gs.info(" checking allTaskCompleted ==> " + allTaskCompleted);
if (allTaskCompleted) {
    var grAccess = new GlideRecordSecure('x_aleen_alertsnowp_building_access');
    grAccess.addQuery('number', current.parent_number);
    grAccess.query();
    while (grAccess.next()) {
        grAccess.state = 3;
        grAccess.update();
    }
}

